'use strict';

var ttn = require('ttn');

var appEUI = '2053415@ttn';
var accessKey = 'NNSXS.IZRLR2Q7CTF5B3C52HSRZYS3LKFCL65FBACOWPQ.K7CFTB6OP7MHKLM35EVJEXB7EI2QNR372OGIZZ2FU3HC7HMHYETA';

var client = new ttn.Client('au1.cloud.thethings.network', appEUI, accessKey);

client.on('connect', function () {
	console.log('[DEBUG]', 'Connected');
});

client.on('error', function (err) {
	console.error('[ERROR]', err.message);
});

client.on('activation', function (e) {
	console.log('[INFO] ', 'Activated: ', e.devEUI);
});

client.on('uplink', function (msg) {
	console.info('[INFO] ', 'Uplink: ' + JSON.stringify(msg, null, 2));
});
client.on('_uplinkDevice680_', function (msg) {
	console.info('[INFO] ', 'Uplink: ' + JSON.stringify(msg, null, 2));
});

client.on('uplink', function (msg) {

	// respond to every third message
	if (msg.counter % 3 === 0) {
		console.log('[DEBUG]', 'Downlink');

		var payload = new Buffer('4869', 'hex');
		client.downlink(msg.devEUI, payload);
	}
});